mapper.select:
select qnano, userid, title, content, hit, answercontent,
		case when date_format(regtime, '%Y%m%d') = date_format(now(), '%Y%m%d')
		then date_format(regtime, '%H:%i:%s')
		else date_format(regtime, '%y.%m.%d')
		end regtime
from qna


		where title like concat('%', #{word}, '%')


		where ${key} = #{word}


order by qnano desc
limit #{start}, #{spp}

select count(qnano) from qna
where title like concat('%', #{word}, '%')
where ${key} = #{word}

select qnano, userid, title, content, hit, regtime, answercontent
from qna
where qnano = #{qnano}

mapper.update:
update qna
set hit = hit + 1
where qnano = #{qnano}

update qna
set title = #{title}, content = #{content}
where qnano = #{qnano}

mapper.insert:
update qna
set answercontent = #{answercontent}
where qnano = #{qnano}
